import React, { useState } from 'react';
import { RecoilRoot } from 'recoil';
import RecordForm from './components/RecordForm';
import RecordList from './components/RecordList';
import SearchBar from './components/SearchBar';
import './styles/App.scss';

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [editRecordId, setEditRecordId] = useState(null); // For tracking which record is being edited

  return (
    <RecoilRoot>
      <div className="app">
        <h1>Client Records Management</h1>
        <RecordForm editRecordId={editRecordId} setEditRecordId={setEditRecordId} />
        <SearchBar setSearchQuery={setSearchQuery} />
        <RecordList searchQuery={searchQuery} setEditRecordId={setEditRecordId} />
      </div>
    </RecoilRoot>
  );
};

export default App;
