import React, { useState } from 'react';
import { useRecoilState } from 'recoil';
import { recordsState } from '../recoil/atoms';

const RecordList = ({ searchQuery, setEditRecordId }) => {
  const [records] = useRecoilState(recordsState);
  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;

  // Filtering records based on search query
  const filteredRecords = records.filter(record => 
    record.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    record.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
    record.id.toString().includes(searchQuery)
  );

  // Pagination logic
  const indexOfLastRecord = currentPage * recordsPerPage;
  const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
  const currentRecords = filteredRecords.slice(indexOfFirstRecord, indexOfLastRecord);
  const totalPages = Math.ceil(filteredRecords.length / recordsPerPage);

  const handleDelete = (id) => {
    // Use setRecords to filter out the deleted record
    setRecords(prevRecords => prevRecords.filter(record => record.id !== id));
  };

  const handleEdit = (id) => {
    // Set the ID of the record to be edited
    setEditRecordId(id);
  };

  return (
    <div>
      <ul>
        {currentRecords.map(record => (
          <li key={record.id}>
            {record.name} - {record.email} 
            <button onClick={() => handleEdit(record.id)}>Edit</button>
            <button onClick={() => handleDelete(record.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <div>
        {Array.from({ length: totalPages }, (_, index) => (
          <button key={index} onClick={() => setCurrentPage(index + 1)}>
            {index + 1}
          </button>
        ))}
      </div>
      <div>
        {currentPage > 1 && (
          <button onClick={() => setCurrentPage(currentPage - 1)}>Previous</button>
        )}
        {currentPage < totalPages && (
          <button onClick={() => setCurrentPage(currentPage + 1)}>Next</button>
        )}
      </div>
    </div>
  );
};

export default RecordList;
