import React, { useState } from 'react';
import { useRecoilState } from 'recoil';
import { recordsState } from '../recoil/atom';

const RecordForm = () => {
  const [records, setRecords] = useRecoilState(recordsState);
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      const jsonData = JSON.parse(e.target.result);
      const uniqueEmails = new Set();
      const mergedRecords = [...records];

      jsonData.forEach(record => {
        if (!uniqueEmails.has(record.email)) {
          uniqueEmails.add(record.email);
          mergedRecords.push(record);
        }
      });

      setRecords(mergedRecords);
    };

    if (file) {
      reader.readAsText(file);
    }
  };

  const handleEditRecord = (id) => {
    const existingRecord = records.find(record => record.id === id);
    if (existingRecord) {
      setName(existingRecord.name);
      setEmail(existingRecord.email);
    }
  };

  const handleUpdateRecord = (id) => {
    const emailExists = records.some(record => record.email === email && record.id !== id);
    if (emailExists) {
      alert("Email must be unique!");
      return;
    }
    setRecords(records.map(record => record.id === id ? { ...record, email, name } : record));
  };

  return (
    <div>
      <input type="file" accept=".json" onChange={handleFileUpload} />
      <div>
        <input 
          type="text" 
          placeholder="Name" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
        />
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
        />
        <button onClick={handleUpdateRecord}>Update Record</button>
      </div>
    </div>
  );
};

export default RecordForm;
